package Operators;

public class PostIncrementation {  //a++

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int a =5;  // post incrementation means adding plus after the value or varable;
   
       int  b= a++;      
       System.out.println(b);//  o/p  becoz its passes the original value ie b=a;
      
	}

}
